emC_PPC[PPCNum].Summary.ComFaultCnt	:=	0	;		
emC_PPC[PPCNum].Summary.OnlineCnt	:=	0	;
emC_PPC[PPCNum].Summary.AvgTempAct	:=	REAL#0.0	;
emC_PPC[PPCNum].Summary.TotBatPNomMaxAct	:=	REAL#0.0	;
emC_PPC[PPCNum].Summary.TotBatPNomMinAct	:=	REAL#0.0	;
emC_PPC[PPCNum].Summary.AvgSocPct	:=	REAL#0.0	;
emC_PPC[PPCNum].Summary.AvgUAct	:=	REAL#0.0	;
emC_PPC[PPCNum].Summary.AvgFAct	:=	REAL#0.0	;
emC_PPC[PPCNum].Summary.TotPAct	:=	REAL#0.0	;
emC_PPC[PPCNum].Summary.TotQAct	:=	REAL#0.0	;



(*	=====	LUC	[1]	==========	=	=	*)
		If NOT emC_PPC[PPCNum].LuC[1].In.ComFault	THEN
			emC_PPC[PPCNum].Summary.GCFaultSt	:=	emC_PPC[PPCNum].Summary.GCFaultSt	OR	emC_PPC[PPCNum].LuC[1].In.GCFaultSt	;
			emC_PPC[PPCNum].Summary.NonCriticalAlarm	:=	emC_PPC[PPCNum].Summary.NonCriticalAlarm	OR	emC_PPC[PPCNum].LuC[1].In.NonCriticalAlarm	;
			emC_PPC[PPCNum].Summary.CriticalAlarm	:=	emC_PPC[PPCNum].Summary.CriticalAlarm	OR	emC_PPC[PPCNum].LuC[1].In.CriticalAlarm	;
			emC_PPC[PPCNum].Summary.Warning	:=	emC_PPC[PPCNum].Summary.Warning	OR	emC_PPC[PPCNum].LuC[1].In.Warning	;
			emC_PPC[PPCNum].Summary.StoppingSt	:=	emC_PPC[PPCNum].Summary.StoppingSt	OR	emC_PPC[PPCNum].LuC[1].In.StoppingSt	;
			emC_PPC[PPCNum].Summary.BlackSys	:=	emC_PPC[PPCNum].Summary.BlackSys	OR	emC_PPC[PPCNum].LuC[1].In.BlackSys	;
		END_IF;
		If NOT emC_PPC[PPCNum].LuC[1].In.ComFault	THEN
			emC_PPC[PPCNum].Summary.ComFaultCnt	:=	emC_PPC[PPCNum].Summary.ComFaultCnt	+	1	;
		END_IF;
		If NOT emC_PPC[PPCNum].LuC[1].In.ComFault AND	emC_PPC[PPCNum].LuC[1].In.OnlineSt 	THEN

				emC_PPC[PPCNum].Summary.OnlineCnt	:=	emC_PPC[PPCNum].Summary.OnlineCnt	+	1	;


				emC_PPC[PPCNum].Summary.AvgTempAct	:=	(emC_PPC[PPCNum].Summary.AvgTempAct	+	emC_PPC[PPCNum].LuC[1].In.TempAct)/INT_TO_REAL(emC_PPC[PPCNum].Summary.OnlineCnt)	;
				emC_PPC[PPCNum].Summary.TotBatPNomMaxAct	:=	emC_PPC[PPCNum].Summary.TotBatPNomMaxAct	+	emC_PPC[PPCNum].LuC[1].In.BatPNomMaxAct	;
				emC_PPC[PPCNum].Summary.TotBatPNomMinAct	:=	emC_PPC[PPCNum].Summary.TotBatPNomMinAct	+	emC_PPC[PPCNum].LuC[1].In.BatPNomMinAct	;
				emC_PPC[PPCNum].Summary.AvgSocPct	:=	(emC_PPC[PPCNum].Summary.AvgSocPct	+	emC_PPC[PPCNum].LuC[1].In.SocPctAct)/INT_TO_REAL(emC_PPC[PPCNum].Summary.OnlineCnt)	;
				emC_PPC[PPCNum].Summary.AvgUAct	:=	(emC_PPC[PPCNum].Summary.AvgUAct	+	emC_PPC[PPCNum].LuC[1].In.UAct)/INT_TO_REAL(emC_PPC[PPCNum].Summary.OnlineCnt)	;
				emC_PPC[PPCNum].Summary.AvgFAct	:=	(emC_PPC[PPCNum].Summary.AvgFAct	+	emC_PPC[PPCNum].LuC[1].In.FAct)/INT_TO_REAL(emC_PPC[PPCNum].Summary.OnlineCnt)	;
				emC_PPC[PPCNum].Summary.TotPAct	:=	emC_PPC[PPCNum].Summary.TotPAct	+	emC_PPC[PPCNum].LuC[1].In.PAct	;
				emC_PPC[PPCNum].Summary.TotQAct	:=	emC_PPC[PPCNum].Summary.TotQAct	+	emC_PPC[PPCNum].LuC[1].In.QAct	;

		END_IF	;	




(*	=====	LUC	[2]	==========	=	=	*)
		If NOT emC_PPC[PPCNum].LuC[2].In.ComFault	THEN						
			emC_PPC[PPCNum].Summary.GCFaultSt	:=	emC_PPC[PPCNum].Summary.GCFaultSt	OR	emC_PPC[PPCNum].LuC[2].In.GCFaultSt	;	
			emC_PPC[PPCNum].Summary.NonCriticalAlarm	:=	emC_PPC[PPCNum].Summary.NonCriticalAlarm	OR	emC_PPC[PPCNum].LuC[2].In.NonCriticalAlarm	;	
			emC_PPC[PPCNum].Summary.CriticalAlarm	:=	emC_PPC[PPCNum].Summary.CriticalAlarm	OR	emC_PPC[PPCNum].LuC[2].In.CriticalAlarm	;	
			emC_PPC[PPCNum].Summary.Warning	:=	emC_PPC[PPCNum].Summary.Warning	OR	emC_PPC[PPCNum].LuC[2].In.Warning	;	
			emC_PPC[PPCNum].Summary.StoppingSt	:=	emC_PPC[PPCNum].Summary.StoppingSt	OR	emC_PPC[PPCNum].LuC[2].In.StoppingSt	;	
			emC_PPC[PPCNum].Summary.BlackSys	:=	emC_PPC[PPCNum].Summary.BlackSys	OR	emC_PPC[PPCNum].LuC[2].In.BlackSys	;	
		END_IF;							
		If NOT emC_PPC[PPCNum].LuC[2].In.ComFault	THEN						
			emC_PPC[PPCNum].Summary.ComFaultCnt	:=	emC_PPC[PPCNum].Summary.ComFaultCnt	+	1	;	
		END_IF;							
		If NOT emC_PPC[PPCNum].LuC[2].In.ComFault AND	emC_PPC[PPCNum].LuC[2].In.OnlineSt 	THEN					

				emC_PPC[PPCNum].Summary.OnlineCnt	:=	emC_PPC[PPCNum].Summary.OnlineCnt	+	1	;

				emC_PPC[PPCNum].Summary.AvgTempAct	:=	(emC_PPC[PPCNum].Summary.AvgTempAct	+	emC_PPC[PPCNum].LuC[2].In.TempAct)/INT_TO_REAL(emC_PPC[PPCNum].Summary.OnlineCnt)	;
				emC_PPC[PPCNum].Summary.TotBatPNomMaxAct	:=	emC_PPC[PPCNum].Summary.TotBatPNomMaxAct	+	emC_PPC[PPCNum].LuC[2].In.BatPNomMaxAct	;
				emC_PPC[PPCNum].Summary.TotBatPNomMinAct	:=	emC_PPC[PPCNum].Summary.TotBatPNomMinAct	+	emC_PPC[PPCNum].LuC[2].In.BatPNomMinAct	;
				emC_PPC[PPCNum].Summary.AvgSocPct	:=	(emC_PPC[PPCNum].Summary.AvgSocPct	+	emC_PPC[PPCNum].LuC[2].In.SocPctAct)/INT_TO_REAL(emC_PPC[PPCNum].Summary.OnlineCnt)	;
				emC_PPC[PPCNum].Summary.AvgUAct	:=	(emC_PPC[PPCNum].Summary.AvgUAct	+	emC_PPC[PPCNum].LuC[2].In.UAct)/INT_TO_REAL(emC_PPC[PPCNum].Summary.OnlineCnt)	;
				emC_PPC[PPCNum].Summary.AvgFAct	:=	(emC_PPC[PPCNum].Summary.AvgFAct	+	emC_PPC[PPCNum].LuC[2].In.FAct)/INT_TO_REAL(emC_PPC[PPCNum].Summary.OnlineCnt)	;
				emC_PPC[PPCNum].Summary.TotPAct	:=	emC_PPC[PPCNum].Summary.TotPAct	+	emC_PPC[PPCNum].LuC[2].In.PAct	;
				emC_PPC[PPCNum].Summary.TotQAct	:=	emC_PPC[PPCNum].Summary.TotQAct	+	emC_PPC[PPCNum].LuC[2].In.QAct	;

		END_IF	;	



(*	=====	LUC	[3]	==========	=	=	*)
		If NOT emC_PPC[PPCNum].LuC[3].In.ComFault	THEN						
			emC_PPC[PPCNum].Summary.GCFaultSt	:=	emC_PPC[PPCNum].Summary.GCFaultSt	OR	emC_PPC[PPCNum].LuC[3].In.GCFaultSt	;	
