Execute_Cmd:= Sel(Execute_Cmd, TRUE,FALSE) ;


(*____________________________________________________*)
FAct_temp := 0.0;
UAct_temp := 0.0;
PAct:=  0.0;
QAct :=  0.0;


TON_1(IN:=not TON_1.Q,PT:=t#5s);


if TON_1.Q and AutoStartCmd and (NextStart < LuCNum) then NextStart := NextStart + 1; AutoStartCmd := FALSE; End_IF;


for i:= 1 to LuCNum do
	emC_PPC[PPCNum].LuC[i].Out.NetOnlineSt := NetOnlineSt;
	emC_PPC[PPCNum].LuC[i].Out.NetIslandReq := NetIslandReq;
	emC_PPC[PPCNum].LuC[i].Out.AutoStartCmd := i <= NextStart;
	emC_PPC[PPCNum].LuC[i].Out.FSetOut	:=	FSetOut;
	emC_PPC[PPCNum].LuC[i].Out.USetOut	:=	USetOut;
	emC_PPC[PPCNum].LuC[i].Out.PSetOut	:=	PSetOut;
	emC_PPC[PPCNum].LuC[i].Out.QSetOut	:=QSetOut;


	FAct_temp := (emC_PPC[PPCNum].LuC[i].In.FAct + FAct_temp) ;
	UAct_temp := (emC_PPC[PPCNum].LuC[i].In.UAct + UAct_temp);
	PAct:= emC_PPC[PPCNum].LuC[i].In.PAct + PAct;
	QAct :=emC_PPC[PPCNum].LuC[i].In.QAct + QAct;
End_For;


FAct := FAct_temp / int_to_Real(LuCNum);
